import React, { Component } from 'react';
import { Card, Heder, Footer } from 'reactstrap';
import Profile from './profile';
import './ListProfile.css';
import axios from 'axios';
import CloseIcon from '@material-ui/icons/Close';
import ReactBSAlert from "react-bootstrap-sweetalert";

export default class ListProfiels extends Component {
    constructor(props) {
        super(props);
        this.state = {
            alert: null,
            profileList: [],
            loading: false,
            page: 0,
            prevY: 0
        };
    }


    // Pop-Ups for response in Modals
    handleResponsePopUp = (message, title, success, error) => {
        this.setState({
            alert: (
                <ReactBSAlert
                    success={success}
                    error={error}
                    style={{ display: "block", marginTop: "250px" }}
                    title={title}
                    onConfirm={() => this.handleModalClose()}
                    confirmBtnBsStyle="success"
                    btnSize="md"
                >
                    {message}
                </ReactBSAlert>
            )
        });
    };

    handleModalClose = () => {
        this.setState({
            alert: null
        });
    };

    handleGetProfileList = () => {
        axios.get('http://localhost:5000/api/profile').then(response => {
            if (response.length < 0) return this.handleResponsePopUp("No records founds", 'Error', false, true);
            this.setState({
                profileList: response.data
            });
        }).catch(error => {
            console.log(error)
            return this.handleResponsePopUp(error.response.data.message, 'Error', false, true);
        });
    }

    componentDidMount = () => {
        this.handleGetProfileList();
    }

    handleDeleteProfile = (_id) => {
        axios.delete(`http://localhost:5000/api/profile/${_id}`).then(response => {
            this.handleGetProfileList();
            return this.handleResponsePopUp(response.message, 'Success', true, false);
        }).catch(error => {
            console.log(error)
            return this.handleResponsePopUp(error.response.data.message, 'Error', false, true);
        });
    }

    render() {
        const { profileList } = this.state;
        return (
            <>
                {this.state.alert}
                <Card className='shadow'>
                    <CloseIcon className='back_btn' onClick={this.props.handleBack} />
                    <div className='profile_details'>
                        <div className='profile_list'>
                            {profileList && profileList.map((item, index) => {
                                return <Profile
                                    title={item.name}
                                    desc={item.description}
                                    channelImage={item.base64_file}
                                    image={item.base64_file}
                                    gender={item.gender}
                                    addDate={item.timestamp}
                                    handleDeleteProfile={(_id) => this.handleDeleteProfile(_id)}
                                    _id={item._id}
                                />
                            })}
                        </div>
                    </div>
                </Card>
            </>
        )
    }
}
