import React, { Component } from 'react';
import { Card, Col, Row, Input, FormGroup, Button, Label, CardTitle, CardHeader, CardFooter } from 'reactstrap';
import './AddDetails.css';
import { verifyAlpha } from '../../util/helper';
import axios from 'axios';
import ReactBSAlert from "react-bootstrap-sweetalert";
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import ListProfile from './ListProfiels';

const ADD_SERVICE_FIELDS = (state) => [
    {
        resultState: state.nameState,
        placeholder: 'Name',
        name: 'name',
        type: 'text',
        condition: 'alpha',
        errorMsg: 'Please enter a valid name.',
        value: 2
    },
    {
        resultState: state.descriptionState,
        placeholder: 'Description',
        name: 'description',
        type: 'textarea',
        condition: 'alpha',
        errorMsg: 'Please enter a profile description.',
        value: 2
    }
]

export default class AddDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            alert: null,
            name: '',
            nameState: '',
            description: '',
            gender: '',
            descriptionState: '',
            fileData: '',
            fileName: '',
            isAddView: true,
            isListView: false
        };
    }


    change = (event, stateName, type, stateNameEqualTo, Value) => {
        switch (type) {
            case "length":
                if (event.target.value.length >= stateNameEqualTo) {
                    this.setState({ [stateName + "State"]: "has-success" });
                } else {
                    this.setState({ [stateName + "State"]: "has-danger" });
                }
                break;
            case "alpha":
                if (verifyAlpha(event.target.value)) {
                    this.setState({ [stateName + "State"]: "has-success" });
                } else {
                    this.setState({ [stateName + "State"]: "has-danger" });
                }
            default:
                break;
        }
        this.setState({ [stateName]: event.target.value });
    };

    isValidated = () => {
        if (
            (this.state.nameState === "has-success") &&
            (this.state.descriptionState === "has-success")
        ) {
            return true;
        } else {
            if (this.state.nameState !== "has-success") {
                this.setState({ nameState: "has-danger" });
            }
            if (this.state.descriptionState !== "has-success") {
                this.setState({ descriptionState: "has-danger" });
            }
            return false;
        }
    };

    handleFileChange = (event) => {
        let selectedFile = event.target.files;
        //Check File is not Empty
        if (selectedFile.length > 0) {
            // Select the very first file from list
            let fileToLoad = selectedFile[0];
            // FileReader function for read the file.
            let fileReader = new FileReader();
            // Onload of file read the file content
            fileReader.onload = (fileLoadedEvent) => {
                this.setState({
                    fileData: fileLoadedEvent.target.result,
                    fileName: selectedFile.name
                })
            };
            // Convert data to base64
            fileReader.readAsDataURL(fileToLoad);
        }
        console.log(this.state.fileData);
    };

    handleSaveProfile = () => {
        this.isValidated()
        const { name, description, fileData, gender } = this.state;
        if (!fileData) return this.handleResponsePopUp('Please select file', 'Error', false, true);
        if (!gender) return this.handleResponsePopUp('Please select gender', 'Error', false, true);
        const postData = {
            name: name,
            description: description,
            base64_file: fileData,
            gender: gender
        }
        const url = 'http://localhost:5000/api/profile';
        axios.post(url, postData).then(response => {
            this.handleClear();
            return this.handleResponsePopUp(response.data.message, 'Success', true, false);
        }).catch(error => {
            return this.handleResponsePopUp(error.response.data.message, 'Error', false, true);
        });
    };

    // Pop-Ups for response in Modals
    handleResponsePopUp = (message, title, success, error) => {
        this.setState({
            alert: (
                <ReactBSAlert
                    success={success}
                    error={error}
                    style={{ display: "block", marginTop: "250px" }}
                    title={title}
                    onConfirm={() => this.handleModalClose()}
                    confirmBtnBsStyle="success"
                    btnSize="md"
                >
                    {message}
                </ReactBSAlert>
            )
        });
    };

    handleModalClose = () => {
        this.setState({
            alert: null
        });
    };

    handleGenderChange = (event) => {
        this.setState({
            gender: event.target.value
        });
    };

    handleChangeView = () => {
        this.setState({
            isAddView: !this.state.isAddView,
            isListView: !this.state.isListView
        });
    };

    handleClear = () => {
        this.setState({
            name: '',
            nameState: '',
            description: '',
            descriptionState: '',
            fileData: '',
            fileName: '',
            gender: '',
        });
    }

    render() {
        const { gender, isAddView, isListView } = this.state;
        return (
            <>
                {this.state.alert}
                <div className='add_details_card'>
                    {isListView ? <CardTitle className='text-center' tag='h2'>List Profiles</CardTitle> : null}
                    {isAddView ? <Card className='shadow'>
                        <CardHeader>
                            <div>
                                <CardTitle className='text-center' tag='h2'>Add Profile</CardTitle>
                                <Button color='info' size='sm' style={{ float: "right" }} className='text-center' onClick={() => this.handleChangeView()}>View Profiles</Button>
                            </div>
                        </CardHeader>
                        <Row className='inputes'>
                            <Col md='5' sm='12' className='ml-auto mr-auto'>
                                {ADD_SERVICE_FIELDS(this.state).map((field, index) => {
                                    return <Col key={index}>
                                        <Label>{field.placeholder}</Label>
                                        <FormGroup className={field.resultState}>
                                            <Input
                                                placeholder={field.placeholder}
                                                name={field.name}
                                                value={this.state[field.name]}
                                                type={field.type}
                                                onChange={e => this.change(e, field.name, field.condition, field.value)}
                                            />
                                            {field.resultState === "has-danger" ? (
                                                <label style={{ color: 'red' }} className="error">{field.errorMsg}</label>
                                            ) : null}
                                        </FormGroup>
                                    </Col>
                                })}
                                <div className='radiobtn'>
                                    <FormControl component="fieldset">
                                        <FormLabel component="legend">Select Gender</FormLabel>
                                        <RadioGroup aria-label="gender" name="gender1" value={gender} onChange={(e) => this.handleGenderChange(e)}>
                                            <FormControlLabel value="female" control={<Radio />} label="Female" />
                                            <FormControlLabel value="male" control={<Radio />} label="Male" />
                                            <FormControlLabel value="other" control={<Radio />} label="Other" />
                                        </RadioGroup>
                                    </FormControl>
                                </div>
                            </Col>
                            <Col md='5' sm='12' className='ml-auto mr-auto'>
                                <div className="fileinput text-center">
                                    <Label>Select File</Label>
                                    <div>
                                        <input type="file" name='file' value={this.state.fileName} className='file_selecter' onChange={(e) => this.handleFileChange(e)} />
                                    </div>
                                </div>
                            </Col>
                        </Row>
                        <CardFooter>
                            <div style={{ float: "right" }}>
                                <Button onClick={() => this.handleClear()} color='danger' size='sm'>Clear</Button>
                                <Button className='ml-3' onClick={() => this.handleSaveProfile()} color='info' size='sm'>Save Profile</Button>
                            </div>
                        </CardFooter>
                    </Card> : null}
                    {isListView ? <ListProfile handleBack={() => this.handleChangeView()} /> : null}
                </div>
            </>
        )
    }
}
