import React, { Component } from 'react'
import Header from './Header';
import Body from './Body';
import Footer from './Footer';

export default class galarry extends Component {
    constructor(props) {
        super(props);
        this.state = {
            alert: null,
            name: '',
            nameState: '',
            description: '',
            gender: '',
            descriptionState: '',
            fileData: '',
            fileName: '',
            isAddView: true,
            isListView: false
        };
    }


    handleFileChange = (event) => {
        console.log(event)
        let selectedFile = event.target.files;
        console.log("--------------",selectedFile)
        //Check File is not Empty
        if (selectedFile.length > 0) {
            // Select the very first file from list
            let fileToLoad = selectedFile[0];
            // FileReader function for read the file.
            let fileReader = new FileReader();
            // Onload of file read the file content
            fileReader.onload = (fileLoadedEvent) => {
                this.setState({
                    fileData: fileLoadedEvent.target.result,
                    fileName: selectedFile.name
                })
            };
            // Convert data to base64
            fileReader.readAsDataURL(fileToLoad);
        }
        console.log(this.state.fileData);
    };



    render() {
        return (
            <div className='galarry'>
                <div className='galarry_header'>
                    <Header />
                </div>
                <div className='galarry_body'>
                    <Body handleUploadFile={(event) => this.handleFileChange(event)} />
                </div>
                <div className='galarry_fotter'>
                    <Footer />
                </div>
            </div>
        )
    }
}
